SELECT ISNULL(SinkRowCount, 0) AS SinkRowCount
FROM dbo.ExtractLoadStatus
WHERE BatchID = '@{pipeline().parameters.task.BatchID}' AND SourceID = @{pipeline().parameters.task.SourceID}

source.sqlReaderQuery:
SELECT [Path], [Index], [Limit] FROM   
( 
	SELECT * FROM [dbo].[Config]
	WHERE [KeyGroup] = 'IngestionSheet'
) T
PIVOT(
	MAX([Value]) 
	FOR [Key] IN (
		[Index], 
		[Path],
		[Limit]
	)
) T;
